import React, { useState } from 'react';

const Editable = ({
  // possible fields for user/creator editing
  text,
  type,
  placeholder,
  children,
  ...props
}) => {
  // manage state to show label(default) or user input
  const [isEditing, setEditing] = useState(false);
  var valVariable = useState(text);
  var val = valVariable(null);

  const handleKeyDown = (event, type) => {
    // handle when key is pressed
  };

  const clear = () => {
    val;
  };

  const updateValue = () => {
    setEditing({
      isEditing: false,
      text: this.state.task,
    });
  };

  const renderEditView = () => {
    return (
      <div
        onBlur={() => setEditing(false)}
        onKeyDown={(e) => handleKeyDown(e, type)}
      >
        {children}
        <button onClick={(e) => clear()}>X</button>
        <button onClick={(e) => updateValue()}>Ok</button>
      </div>
    );
  };

  const renderDefault = () => {
    return (
      <div onDoubleClick={() => setEditing(true)}>
        <span>{text || placeholder || 'Editable content'}</span>
      </div>
    );
  };

  return (
    <section {...props}>
      {isEditing ? renderEditView() : renderDefault()}
    </section>
  );
};

export default Editable;
