import React, { useState } from 'react';

function Input() {
  // state to store value from inputs
  const [inputValue, setInputValue] = useState('');
  const [showBtn, setShowBtn] = useState(true);

  const showEdit = () => {
    <div>
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
      />
      <button onClick={setInputValue('')}>X</button>
      <button onClick={setShowBtn(false)}>Ok</button>
    </div>;
  };

  const showVal = () => {
    <div>
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
      />
    </div>;
  };

  return (
    <div>
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
      />
      <button onClick={setInputValue('')}>X</button>
      <button onClick={setShowBtn(false)}>Ok</button>
    </div>
  );
}

export default Input;
